INSERT INTO `dp_posts` VALUES("23", "1", "2022-05-25 08:20:50", "2022-05-25 05:20:50", "<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Minusta:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Muutin Suomeen Dublinista, Irlannista vuonna 2011 tarkoituksenani laajentaa musiikkiuraani rakastamassani maassa. Saavutin tavoitteeni toimia itseni tyllistvn muusikkona ja musiikintuottajana yli 10 vuoden ajan Suomessa ja muissa Euroopan maissa. Johdin pienyrittjn onnistuneesti useita alani ammattilaisia ja heist koostuneita ryhmi erilaisissa tehtviss. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Koronaviruksen rantauduttua Suomeen olen siirtynyt takaisin alkuperisen intohimoni eli tietotekniikan ja ohjelmoimisen pariin. Viimeisen typaikkani oli CGI:ll (Service Desk Specialist:ina, 2022). Nyt opiskelen Taitotalolla, mutta opiskelut lopettavat jo keskuussa. Tutkinto on Tieto- ja viestinttekniikan tutkinto, Ohjelmistokehittj. Valmistun viimeistn kevll 2023.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>IT-Osaaminen:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p><strong>Ohjelmointikielet:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>HTML</li><li>JavaScript (React)</li><li>Node.js</li><li>Express.js</li><li>CSS</li><li>Bootstrap</li><li>PHP</li><li>MySQL</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>GitHub -sivut:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><a href=\"https://github.com/DavittBarry/ruokatori\">https://github.com/DavittBarry/ruokatori</a></li><li><a href=\"https://github.com/EdvardJ/PlantPlanner\">https://github.com/EdvardJ/PlantPlanner</a></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Yleist:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Kyttjrjestelmien ja ohjelmistojen asentaminen.</li><li>Tulostimien ja skannereiden asentaminen.</li><li>Kyttjtilien hallinnoiminen Active Directoryn ja muiden tilinhallintatykalujen avulla.</li><li>Verkkototeutusten suunnitteleminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Laitteisto:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Tyasemien kokoaminen.</li><li>Valmiiden tyasemien muutostyt.</li><li>Ylimristen komponenttien integroiminen tyasemiin.</li><li>Komponenttien vianmritys.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Verkot:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Tietoverkkojen asennustyt.</li><li>Kyttjtunnusten luominen tietoverkkoihin</li><li>Kytkinten ja HUB:ien asentaminen.</li><li>Kyttjrjestelmien pivittminen.</li><li>Laitteiston ja komponenttien vianmritys.</li><li>Tulostimien vianmritys.</li><li>Langattomien yhteyspisteiden asentaminen ja konfiguroiminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Sovellukset:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>MS Excel (mys Pivot-taulukot ja raporttien luominen).</li><li>MS Outlook, MS Word, MS SharePoint.</li><li>Tikettijrjestelmt kuten: ServiceNow, OVSD ja OVSC.</li><li>Windows Remote Desktop, Citrix, VNC ja NetMeeting.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Agile-tuotteenomistajana: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Sprinttien jrjestminen tyryhmlle.</li><li>Tyryhmn jsenten potentiaalin realisoiminen hyvll johtamisella.</li><li>Jira:n kyttminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Kielitaito:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:list -->\n<ul><li><strong>Englanti</strong><ul><li>idinkieli.</li></ul></li><li><strong>Suomi</strong><ul><li>Luksia Oy:n suomen kielen keskitason tutkinnon (2019):<ul><li>Puheen ymmrtminen: 4/4</li><li>Puhuminen: 4/4</li><li>Tekstin ymmrtminen: 4/4</li><li>Kirjoittaminen: 3/4</li></ul></li></ul></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Koulutus:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table -->\n<figure class=\"wp-block-table\"><table><tbody><tr><td><strong>Maalis 2021  viel jatkuu (2023):</strong></td><td>Taitotalo, Helsinki. Tieto- ja viestinttekniikan<br>tutkinto, Ohjelmistokehittj</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":74,\"sizeSlug\":\"medium\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-medium\"><img src=\"https://davittb.nube.tai