INSERT INTO `dp_posts` VALUES("79", "1", "2022-06-01 09:28:23", "2022-06-01 06:28:23", "<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>Dec 2021  April 2022</strong>:</td><td><em>Clevry/CGI</em></td></tr><tr><td><strong>Role:&nbsp;</strong></td><td>Service Desk Specialist</td></tr><tr><td><strong>Duties:</strong></td><td> Helping end-users with all computer-related issues.<br> Usage of the ServiceNow system to categorize tickets.<br> Guidance to users on how to operate CGI systems.<br> Creation and modification of end-point and AD accounts.<br> Establishing remote connections to users.<br> Following up of tickets on the teams que.<br> My blog about my duties at CGI: <a href=\"https://sddavitt.wordpress.com/\">https://sddavitt.wordpress.com/</a></td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>April 2022  current</strong>:</td><td>PlantPlanner<em>, Taitotalo Web11 dev team</em></td></tr><tr><td><strong>Role:&nbsp;</strong></td><td>Developer</td></tr><tr><td></td><td> Using Jira to plan team sprints and project goals for a 1 year and a half duration.<br> Keeping teammates up to date with duties and goals.<br> Project organization and direction of project execution procedures.<br> GitHub page: <a href=\"https://github.com/EdvardJ/PlantPlanner\">https://github.com/EdvardJ/PlantPlanner</a><br> Dev page: <a href=\"https://plantplanner287818493.wordpress.com/dokumentaatio/\">https://plantplanner287818493.wordpress.com/dokumentaatio/</a></td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>August 2021  current</strong>:</td><td><em>Ruokatori, Taitotalo Web11 dev team</em></td></tr><tr><td><strong>Role:</strong></td><td>Agile Product Owner</td></tr><tr><td><strong>Duties:</strong></td><td> Using Jira to plan team sprints and project goals for a 1 year and a half duration.<br> Keeping teammates up to date with duties and goals.<br> Project organization and direction of project execution procedures.<br> GitHub page: <a href=\"https://github.com/DavittBarry/ruokatori\">https://github.com/DavittBarry/ruokatori</a><br> Dev page:<a href=\"https://eduamiedu-my.sharepoint.com/personal/davitt_barry_edu_taitotalo_fi/Documents/CV%20kansio/\"> </a><a href=\"https://ruokatoridevblogi.wordpress.com/\">https://ruokatoridevblogi.wordpress.com/ </a>&nbsp;</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>July 2011&nbsp; &nbsp;&nbsp; March 2020</strong>:</td><td><em>Piikki Kasvi/Sahara Oy  Silja Line/Viking Line/Tallink</em></td></tr><tr><td><strong>Role:&nbsp;</strong></td><td>Musician/music producer/entertainment sales&nbsp;</td></tr><tr><td><strong>Duties:&nbsp;</strong></td><td> Musician in many roles and bands/events.<br> Entertainment cruises on the Baltic Sea, but also many events throughout Finland.<br> Event logistics and planning.<br> Music production and distribution.<br> Contacting clients via phone and organizing events.<br> Developing websites/promo for bands.<br> Sound engineering audio and video demos for bands.<br> Organizing transport to venues.<br> Updating bands on contracts</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=