INSERT INTO `dp_posts` VALUES("130", "1", "2022-06-13 08:35:55", "2022-06-13 05:35:55", "<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Minusta:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Muutin Suomeen Dublinista, Irlannista vuonna 2011 tarkoituksenani laajentaa musiikkiuraani rakastamassani maassa. Saavutin tavoitteeni toimia itseni tyllistvn muusikkona ja musiikintuottajana yli 10 vuoden ajan Suomessa ja muissa Euroopan maissa. Johdin pienyrittjn onnistuneesti useita alani ammattilaisia ja heist koostuneita ryhmi erilaisissa tehtviss. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Koronaviruksen rantauduttua Suomeen olen siirtynyt takaisin alkuperisen intohimoni eli tietotekniikan ja ohjelmoimisen pariin. Viimeisen typaikkani oli CGI:ll (Service Desk Specialist:ina, 2022). Nyt opiskelen Taitotalolla, mutta opiskelut lopettavat jo keskuussa. Tutkinto on Tieto- ja viestinttekniikan perustutkinto, Ohjelmistokehittj. Valmistun viimeistn kevll 2023.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>IT-Osaaminen:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p><strong>Ohjelmointikielet:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>HTML</li><li>JavaScript (React)</li><li>Node.js</li><li>Express.js</li><li>CSS</li><li>Bootstrap</li><li>PHP</li><li>MySQL</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>GitHub -sivut:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><a href=\"https://github.com/DavittBarry/ruokatori\">https://github.com/DavittBarry/ruokatori</a></li><li><a href=\"https://github.com/EdvardJ/PlantPlanner\">https://github.com/EdvardJ/PlantPlanner</a></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Yleist:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Kyttjrjestelmien ja ohjelmistojen asentaminen.</li><li>Tulostimien ja skannereiden asentaminen.</li><li>Kyttjtilien hallinnoiminen Active Directoryn ja muiden tilinhallintatykalujen avulla.</li><li>Verkkototeutusten suunnitteleminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Laitteisto:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Tyasemien kokoaminen.</li><li>Valmiiden tyasemien muutostyt.</li><li>Ylimristen komponenttien integroiminen tyasemiin.</li><li>Komponenttien vianmritys.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Verkot:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Tietoverkkojen asennustyt.</li><li>Kyttjtunnusten luominen tietoverkkoihin</li><li>Kytkinten ja HUB:ien asentaminen.</li><li>Kyttjrjestelmien pivittminen.</li><li>Laitteiston ja komponenttien vianmritys.</li><li>Tulostimien vianmritys.</li><li>Langattomien yhteyspisteiden asentaminen ja konfiguroiminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Sovellukset:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>MS Excel (mys Pivot-taulukot ja raporttien luominen).</li><li>MS Outlook, MS Word, MS SharePoint.</li><li>Tikettijrjestelmt kuten: ServiceNow, OVSD ja OVSC.</li><li>Windows Remote Desktop, Citrix, VNC ja NetMeeting.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Agile-tuotteenomistajana: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Sprinttien jrjestminen tyryhmlle.</li><li>Tyryhmn jsenten potentiaalin realisoiminen hyvll johtamisella.</li><li>Jira:n kyttminen.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Kielitaito:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:list -->\n<ul><li><strong>Englanti</strong><ul><li>idinkieli.</li></ul></li><li><strong>Suom</strong>i<ul><li>Luksia Oy:n suomen kielen keskitason tutkinnon (2019):<ul><li>Puheen ymmrtminen: 4/4</li><li>Puhuminen: 4/4</li><li>Tekstin ymmrtminen: 4/4</li><li>Kirjoittaminen: 3/4</li></ul></li></ul></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Koulutus:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table -->\n<figure class=\"wp-block-table\"><table><tbody><tr><td><strong>Maalis 2021  viel jatkuu (2023):</strong></td><td>Taitotalo, Helsinki. Tieto- ja viestinttekniikan<br>perustutkinto, Ohjelmistokehittj</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":74,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://davittb.nube.taitotalo-ict.fi/davitt-portfolio/wp-content/uploads/2022/06/davitts_album_no3_FULLRES_4000x4000-e1654064680492-1024x243.png\" alt=\"\" class=\"wp-image-74\"/></figure>\n<!-- /wp:image -->", "Profiili", "", "inherit", "closed", "closed", "", "23-revision-v1", "", "", "2022-06-13 08:35:55", "2022-06-13 05:35:55", "", "23", "https://davittb.nube.taitotalo-ict.fi/davitt-portfolio/?p=130", "0", "revision", "", "0");
INSERT INTO `dp_posts` VALUES("131", "1", "2022-06-13 08:38:17", "2022-06-13 05:38:17", "<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>About me:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p>I moved to Finland from Dublin, Ireland in 2011 with the intention of expanding my music career in a country I love very much. I achieved in my goals of becoming a completely self-employed musician and music producer for over 10 years in Finland and countries across Europe.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Since covid hit, I have been moving back to my original favorite interest which is ICT and programming. My most recent job was with CGI as a Service Desk Specialist (2022). Now, I am a student with Taitotalo studying a bachelors degree in Information Communications Technology and Programming. I am to complete the studies around spring 2023.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>IT skills:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p><strong>Programming languages:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>HTML</li><li>JavaScript (React)</li><li>Node.js</li><li>Express.js</li><li>CSS</li><li>Bootstrap</li><li>PHP</li><li>MySQL</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>GitHub pages:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><a href=\"https://github.com/DavittBarry/ruokatori\">https://github.com/DavittBarry/ruokatori</a></li><li><a href=\"https://github.com/EdvardJ/PlantPlanner\">https://github.com/EdvardJ/PlantPlanner</a></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>General:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Installation of many different operating systems and applications.</li><li>Installing printing and scanning machines.</li><li>Managing user accounts in Active directory and other administrative tools.</li><li>Website designing.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Computer construction:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Building new computer systems.</li><li>Modification of prebuilt computers systems.</li><li>Integrate hardware from redundant systems into other PCs.</li><li>Component error troubleshooting.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Networking:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>General Setup of Networks.</li><li>Constructing Cat5 (RJ45) Cables.</li><li>Structured Cabling.</li><li>Installing Switches &amp; Hubs.</li><li>Creating user accounts on a network.</li><li>Updating Operating Systems.</li><li>Hardware Troubleshooting.</li><li>Troubleshooting and resolving network Printer issues.</li><li>Wireless connection setup and configuration.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Applications:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Proficient in Microsoft Excel using Pivot tables, generating reports etc.</li><li>Proficient in using Microsoft Outlook, Word and SharePoint.</li><li>High level of skill in ticket systems such as: ServiceNow, OVSD and OVSC.</li><li>Remote tools such as Windows Remote Desktop, Bomgar, Citrix, VNC ja NetMeeting.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>Agile Product Owner skills:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Organizing sprints for the team.</li><li>Facilitating teammates so they reach their max potential.</li><li>Usage of systems such as Jira for team and product organizing.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Language skills:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:list -->\n<ul><li><strong>English</strong><ul><li>Mother tongue.</li></ul></li><li><strong>Finnish</strong><ul><li>Luksia Oy Finnish language average level exam (2019):<ul><li><strong>Understanding speech: 4/4</strong></li><li><strong>Speaking: 4/4</strong></li><li><strong>Understanding text: 4/4</strong></li><li><strong>Writing: 3/4&nbsp;</strong></li></ul></li></ul></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"x-large\"} -->\n<p class=\"has-text-align-center has-x-large-font-size\"><strong>Further education:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table -->\n<figure class=\"wp-block-table\"><table><tbody><tr><td><strong>March 2021  In progress (2023)</strong>:</td><td>Taitotalo, Helsinki. Bachelors Degree in Information and Communication Technology, Software Developer.</td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":74,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-large\"><img src=\"https://davittb.nube.taitotalo-ict.fi/davitt-portfolio/wp-content/uploads/2022/06/davitts_album_no3_FULLRES_4000x4000-e1654064680492-1024x243.png\" alt=\"\" class=\"wp-image-74\"/></figure>\n<!-- /wp:image -->", "Profile", "", "inherit", "closed", "closed", "", "19-revision-v1", "", "", "2022-06-13 08:38:17", "2022-06-13 05:38:17", "", "19", "https://davittb.nube.taitotalo-ict.fi/davitt-portfolio/?p=131", "0", "revision", "", "0");
