INSERT INTO `dp_posts` VALUES("234", "1", "2022-08-12 09:09:23", "2022-08-12 06:09:23", "<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>Dec 2021  April 2022</strong>:</td><td><em>Clevry/CGI</em></td></tr><tr><td><strong>Role:&nbsp;</strong></td><td>Service Desk Specialist</td></tr><tr><td><strong>Duties:</strong></td><td> Helping end-users with all computer-related issues.<br> Usage of the ServiceNow system to categorize tickets.<br> Guidance to users on how to operate CGI systems.<br> Creation and modification of end-point and AD accounts.<br> Establishing remote connections to users.<br> Following up of tickets on the teams que.<br> My blog about my duties at CGI: <a href=\"https://sddavitt.wordpress.com/\">https://sddavitt.wordpress.com/</a></td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:table {\"align\":\"center\"} -->\n<figure class=\"wp-block-table aligncenter\"><table><tbody><tr><td><strong>April 2022  current</strong>:</td><td>PlantPlanner<em>, Taitotalo Web11 dev team</em></td></tr><tr><td><strong>Role:&nbsp;</strong></td><td>Developer</td></tr><tr><td></td><td> Using Jira to plan team sprints and project goals for a 1 year and a half duration.<br> Keeping teammates up to date with duties and goals.<br> Project organization and direction of project execution procedures.<br> GitHub page: <a href=\"https://github.com/EdvardJ/PlantPlanner\">https://github.com/EdvardJ/PlantPlanner</a><br> Dev page: <a href=\"https://plantplanner287818493.wordpress.com/dokumentaatio/\">https://plantplanner287818493.wordpress.com/dokumentaatio/</a></td></tr></tbody></table></figure>\n<!-- /wp:table -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channe