INSERT INTO `dp_wc_admin_notes` VALUES("25", "ppxo-pps-upgrade-paypal-payments-1", "info", "en_US", "Get the latest PayPal extension for WooCommerce", "Heads up! Theres a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("26", "ppxo-pps-upgrade-paypal-payments-2", "info", "en_US", "Upgrade your PayPal experience!", "Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">latest PayPal today</a> to continue to receive support and updates.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("27", "woocommerce-core-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("28", "woocommerce-blocks-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("29", "woocommerce-core-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("30", "woocommerce-blocks-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("31", "habit-moment-survey", "marketing", "en_US", "Were all ears! Share your experience so far with WooCommerce", "Wed love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("32", "ecomm-wc-navigation-survey", "info", "en_US", "Wed like your feedback on the WooCommerce navigation", "Were making improvements to the WooCommerce navigation and would love your feedback. Share your experience in this 2 minute survey.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("33", "woocommerce-core-paypal-march-2022-updated", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). Its recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("34", "woocommerce-core-paypal-march-2022-updated-nopp", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("35", "pinterest_03_2022_update", "marketing", "en_US", "Your Pinterest for WooCommerce plugin is out of date!", "Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on update now under Pinterest for WooCommerce.", "{}", "pending", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("36", "setup_task_initiative_survey_q2_2022", "survey", "en_US", "We want to know what matters most to you", "Take 2 minutes to give us your input on what is important for you while setting up your store and help shape the future of WooCommerce together.", "{}", "unactioned", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("37", "store_setup_survey_survey_q2_2022", "survey", "en_US", "How is your store setup going?", "Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWed love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.", "{}", "unactioned", "woocommerce.com", "2022-06-20 09:14:29", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("38", "wc-admin-EU-consumer-protection", "marketing", "en_US", "Important changes to EU consumer protection laws", "New regulations to help modernize and strengthen consumer protection laws in the European Union (EU) take effect on May 28, 2022. These rules impact all merchants selling to the EU, regardless of where their business is located. Further detailed information is available on the European Commission\'s official website.", "{}", "unactioned", "woocommerce.com", "2022-06-13 05:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("39", "wc-admin-launch-checklist", "info", "en_US", "Ready to launch your store?", "To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("40", "wc-admin-choosing-a-theme", "marketing", "en_US", "Choosing a theme?", "Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("41", "wc-admin-insight-first-product-and-payment", "survey", "en_US", "Insight", "More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("42", "wc-admin-customizing-product-catalog", "info", "en_US", "How to customize your product catalog", "You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("43", "wc-admin-first-downloadable-product", "info", "en_US", "Learn more about digital/downloadable products", "Congrats on adding your first digital product! You can learn more about how to handle digital or downloadable products in our documentation.", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("44", "wc-admin-update-store-details", "info", "en_US", "Edit your store details if you need to", "Nice work completing your store profile! You can always go back and edit the details you just shared, as needed.", "{}", "unactioned", "woocommerce-admin", "2022-06-14 11:40:35", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("45", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.", "{}", "unactioned", "woocommerce-admin", "2022-06-15 09:13:36", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("46", "wc-admin-wisepad3", "marketing", "en_US", "Take your business on the go in Canada with WooCommerce In-Person Payments", "Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory  no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.", "{}", "pending", "woocommerce.com", "2022-06-15 09:13:36", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("47", "wc-admin-onboarding-payments-reminder", "info", "en_US", "Start accepting payments on your store!", "Take payments with the provider thats right for you - choose from 100+ payment gateways for WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2022-06-18 17:10:39", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("48", "wc-admin-insight-first-sale", "survey", "en_US", "Did you know?", "A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?", "{}", "unactioned", "woocommerce-admin", "2022-06-20 09:14:29", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("49", "TikTok q2_2022", "marketing", "en_US", "Give your store a stage on the worlds fastest-growing advertising channel", "With TikTok for WooCommerce, you can sync your catalog, create videos, and track performance in front of TikToks one billion global users. Try the Smart Video Generator to make ads using your existing product images  no camera needed. Get $200 in ad credit from TikTok after a $20 spend (terms &amp; conditions apply).", "{}", "unactioned", "woocommerce.com", "2022-06-22 10:40:33", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("50", "wc-admin-customize-store-with-blocks", "info", "en_US", "Customize your online store with WooCommerce blocks", "With our blocks, you can select and display products, categories, filters, and more virtually anywhere on your site  no need to use shortcodes or edit lines of code. Learn more about how to use each one of them.", "{}", "unactioned", "woocommerce-admin", "2022-06-27 06:26:25", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("51", "stripe-apple-pay-domain-verification-needed", "info", "en_US", "Apple Pay domain verification needed", "The WooCommerce Stripe Gateway extension attempted to perform domain verification on behalf of your store, but was unable to do so. This must be resolved before Apple Pay can be offered to your customers.", "{}", "unactioned", "woocommerce-gateway-stripe", "2022-06-28 05:56:01", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("52", "wc-admin-orders-milestone", "info", "en_US", "First order received", "Congratulations on getting your first order! Now is a great time to learn how to manage your orders.", "{}", "unactioned", "woocommerce-admin", "2022-06-28 06:39:32", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("57", "wc-admin-new-sales-record", "info", "en_US", "New sales record!", "Woohoo, July 4th was your record day for sales! Net sales was 20,00 beating the previous record of 5,00 set on June 28th.", "{\"old_record_date\":\"2022-06-28\",\"old_record_amt\":5,\"new_record_date\":\"2022-07-04\",\"new_record_amt\":20}", "unactioned", "woocommerce-admin", "2022-07-05 13:34:13", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("58", "tiktok-webinar-promo-july2022", "marketing", "en_US", "Free webinar: TikTok for WooCommerce", "Join WooCommerce and TikTok live on July 14 to learn about ad types, trends, and the best ways to connect with an audience of one billion active users on the worlds fastest-growing social platform. Participants have a chance to score a free month of Vimeo Pro, and two winners will get a $5,000 ad credit and more.", "{}", "pending", "woocommerce.com", "2022-07-10 18:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("59", "mercado_pago_q3_2022", "marketing", "en_US", "Get paid with Mercado Pago Checkout", "Give your customers a checkout they can trust with Latin America\'s leading payment processor. Securely accept debit and credit cards, cash, bank transfers, and installment payments  backed by exclusive fraud prevention tools.", "{}", "pending", "woocommerce.com", "2022-08-09 03:40:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("60", "klarna_q3_2022", "marketing", "en_US", "Meet Klarna  your ultimate growth partner", "Increase conversions by offering secure, flexible payment solutions  including buy now, pay later  all through a one-click checkout experience. Plus, youll tap into a whole new market of the worlds most engaged shoppers.", "{}", "unactioned", "woocommerce.com", "2022-08-09 03:40:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("61", "woocommerce-payments-august-2022-need-to-update", "update", "en_US", "Action required: Please update WooCommerce Payments", "An updated secure version of WooCommerce Payments is available  please ensure that youre using the latest patch version. For more information on what action you need to take, please review the article below.", "{}", "pending", "woocommerce.com", "2022-08-11 06:54:18", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("62", "woocommerce-payments-august-2022-store-patched", "update", "en_US", "WooCommerce Payments has been automatically updated", "Youre now running the latest secure version of WooCommerce Payments. Weve worked with the WordPress Plugins team to deploy a security update to stores running WooCommerce Payments (version 3.9 to 4.5). For further information, please review the article below.", "{}", "pending", "woocommerce.com", "2022-08-11 06:54:18", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `dp_wc_admin_notes` VALUES("63", "wc-update-db-reminder", "update", "en_US", "WooCommerce database update done", "WooCommerce database update complete. Thank you for updating to the latest version!", "{}", "actioned", "woocommerce-core", "2022-08-11 13:00:38", NULL, "0", "plain", "", "0", "1", "info");
